// Function to find relevant answer based on user input and selected language
export function findAnswer(userInput: string, selectedLanguage: string): string | null {
  const input = userInput.toLowerCase();
  const allEntries = [...knowledgeBase, ...frequentlyAskedQuestions];
  
  for (const entry of allEntries) {
    const hasKeyword = entry.keywords.some(keyword => 
      input.includes(keyword.toLowerCase())
    );
    
    if (hasKeyword && entry.answers[selectedLanguage]) {
      return entry.answers[selectedLanguage];
    }
  }
  
  return null;
}